node CREATED="1697020583448" ID="ID_d7f1bc01aaf6f004" MODIFIED="1697020583448" TEXT="9/19-9/20"><node CREATED="1697020583448" ID="ID_b7825bedb269cd6e" MODIFIED="1697020583448" TEXT="&amp;lt;br&amp;gt;"></node></node><node CREATED="1697020583448" ID="ID_a3183bcb5dbdb7f4" MODIFIED="1697020583448" TEXT="9/26-9/28"><node CREATED="1697020583448" ID="ID_068e4522cb24fb77" MODIFIED="1697020583448" TEXT="&amp;lt;br&amp;gt;"></node></node><node CREATED="1697020583448" ID="ID_22b6c42ce1480e8b" MODIFIED="1697020583448" TEXT="现在"><node CREATED="1697020583448" ID="ID_6461332535d5f796" MODIFIED="1697020583448" TEXT="&amp;lt;br&amp;gt;"></node></node></node></node><node CREATED="1697020583448" ID="ID_67333ecedfeba293" MODIFIED="1697020583448" TEXT="总分析"><node CREATED="1697020583448" ID="ID_19f76bd335112982" MODIFIED="1697020583448" TEXT="关键词花西子、眉笔、国货等提及最多，且结合社交印象分析，提及这次词语时候68.3%为消极情感"></node><node CREATED="1697020583448" ID="ID_d884018d911c938b" MODIFIED="1697020583448" TEXT="关键词孙珍妮提及次数是最多的，估计因为代言而有粉丝在不断发博"></node><node CREATED="1697020583448" ID="ID_d4dc0734451250c9" MODIFIED="1697020583448" TEXT="提及散粉和品牌等，皆为积极感情"></node></node></node></node><node CREATED="1697020583448" ID="ID_4474e7ee4278d820" MODIFIED="1697020583448" TEXT="社交印象分析"><node CREATED="1697020583448" ID="ID_37062b99b33baf61" MODIFIED="1697020583448" TEXT="情感分析"><node CREATED="1697020583448" ID="ID_8125cd84cb29a48a" MODIFIED="1697020583448" TEXT="判断微博内容和评论中用户对该事件的情感倾向（正面、负面）"></node></node><node CREATED="1697020583448" ID="ID_0819c0ca377feafc" MODIFIED="1697020583448" TEXT="语义分析"><node CREATED="1697020583448" ID="ID_38e0a01c6e8431cb" MODIFIED="1697020583448" TEXT="提取微博内容和评论中用户对该商业热点营销事件的主要观点和评价"></node><node CREATED="1697020583448" ID="ID_a517ad6997eff183" MODIFIED="1697020583448" TEXT="提取李佳琦和花西子品牌的认知、美誉、种草等方面的影响，以反映用户对该事件的看法和行为"></node></node></node></node><node CREATED="1697020583448" ID="ID_b5c70c181a8b200f" MODIFIED="1697020583448" TEXT="时间维度节点划分"><node CREATED="1697020583448" ID="ID_e76917838922841c" MODIFIED="169